#include <iostream>
#include <set>

using namespace std;

int partOne() {
    int answer = 0;
    set<char> chars;
    string input = "";
    for (int i = 0; i <= input.size() - 4; i++) {
        chars.clear();
        chars.insert(input[i]);
        chars.insert(input[i+1]);
        chars.insert(input[i+2]);
        chars.insert(input[i+3]);

        if (chars.size() == 4)
            return i + 4;
    }
    return answer;
}

int partTwo() {
    int answer = 0;
    set<char> chars;
    string input = "";
    for (int i = 0; i <= input.size() - 14; i++) {
        chars.clear();
        for (int j = i; j < i + 14; j++) {
            chars.insert(input[j]);
        }

        if (chars.size() == 14)
            return i + 14;
    }
    return answer;
}

int main() {
    cout << "Part one: " << partOne() << endl;
    cout << "Part two: " << partTwo() << endl;
    return 0;
}